# Name        : JoinDomain.ps1
# Author      : K Sarat Kumar
# Version     : 1.0
# Description : Script is used to Change hostname of machines and join to domain.
##############################################
$HOSTNAME = "@@{name}@@-@@{calm_random}@@"

function Set-Hostname{
  [CmdletBinding()]
  Param(
      [parameter(Mandatory=$true)]
      [string]$Hostname
)
  if ($Hostname -eq  $(hostname)){
    Write-Host "Hostname already set."
  } else{
    Rename-Computer -NewName $HOSTNAME -ErrorAction Stop
  }
}

function JointoDomain {
  [CmdletBinding()]
  Param(
      [parameter(Mandatory=$true)]
      [string]$DomainName,
      [parameter(Mandatory=$false)]
      [string]$OU,
      [parameter(Mandatory=$true)]
      [string]$Username,
      [parameter(Mandatory=$true)]
      [string]$Password,
      [parameter(Mandatory=$true)]
      [string]$Server
  )
  $adapter = Get-NetAdapter | ? {$_.Status -eq "up"}
  $adapter | Set-DnsClientServerAddress -ServerAddresses $Server

  if ($env:computername  -eq $env:userdomain) {
    Write-Host "Not in domain"
    $adminname = "$DomainName\$Username"
    $adminpassword = ConvertTo-SecureString -asPlainText -Force -String "$Password"
    Write-Host "$adminname , $password"
    $credential = New-Object System.Management.Automation.PSCredential($adminname,$adminpassword)
    Add-computer -DomainName $DomainName -Credential $credential -force -Options JoinWithNewName,AccountCreate -PassThru -ErrorAction Stop
  } else {
     Write-Host "Already in domain"
  }
}

if ($HOSTNAME -ne $Null){
  Write-Host "Setting Hostname"
  Set-Hostname -Hostname $HOSTNAME
}

JointoDomain -DomainName "@@{DOMAIN}@@" -Username "@@{DOMAIN_CRED.username}@@" -Password "@@{DOMAIN_CRED.secret}@@" -Server "@@{AD_IP}@@"

Restart-Computer -Force -AsJob
exit 0

$PrismIP = "@@{PE_IP}@@"
$PrismUser = "@@{PE_CREDS.username}@@"
$PrismPass = "@@{PE_CREDS.secret}@@"

add-type @"
    using System.Net;
    using System.Security.Cryptography.X509Certificates;
    public class TrustAllCertsPolicy : ICertificatePolicy {
        public bool CheckValidationResult(
            ServicePoint srvPoint, X509Certificate certificate,
            WebRequest request, int certificateProblem) {
            return true;
        }
    }
"@
[System.Net.ServicePointManager]::CertificatePolicy = New-Object TrustAllCertsPolicy

[Net.ServicePointManager]::SecurityProtocol = [Net.SecurityProtocolType]::Tls12

$BaseURL = "https://" + $PrismIP + ":9440/PrismGateway/services/rest/v2.0/"
$Header = @{"Authorization" = "Basic "+[System.Convert]::ToBase64String([System.Text.Encoding]::UTF8.GetBytes($PrismUser+":"+$PrismPass))}
$Type = "application/json"

$VolumeGroupURL = $BaseURL+"volume_groups"
$ContainerURL = $BaseURL+"storage_containers"

$ContainerList = invoke-RestMethod -Uri $ContainerURL -Method Get -TimeoutSec 100 -Headers $Header -ContentType $Type
$ContainerUUID = $ContainerList.entities | where { $_.name -eq "@@{Nutanix_Container_Name}@@"} | select storage_container_uuid
$ContainerUUID = $ContainerUUID.storage_container_uuid

$DB_DiskSize = @@{DB_Disk_Size_GB}@@*1024*1024*1024
$Log_DiskSize = @@{Log_Disk_Size_GB}@@*1024*1024*1024

$Body = @"
{
  "disk_list": [
    {
      "create_config": {
        "size": 1073741824,
	"storage_container_uuid": "$ContainerUUID"
      },
      "index": 0
    },
    {
      "create_config": {
        "size": $DB_DiskSize,
	"storage_container_uuid": "$ContainerUUID"
      },
      "index": 1
    },    
    {
      "create_config": {
        "size": $Log_DiskSize,
	"storage_container_uuid": "$ContainerUUID"
      },
      "index": 2
    }
  ],
  "is_shared": true,
  "name": "@@{Volume_GroupName}@@"
}
"@

Invoke-RestMethod -Uri $VolumeGroupURL -Method Post -TimeoutSec 100 -Headers $Header -ContentType $Type -Body $Body

$MsSQL1_iSCSI_IQN = (Get-InitiatorPort).NodeAddress

$PrismIP = "@@{PE_IP}@@"
$PrismUser = "@@{PE_CREDS.username}@@"
$PrismPass = "@@{PE_CREDS.secret}@@"

add-type @"
