$Type = "application/json"

$VolumeGroupURL = $BaseURL+"volume_groups"

$VolumeGroupList = invoke-RestMethod -Uri $VolumeGroupURL -Method Get -TimeoutSec 100 -Headers $Header -ContentType $Type
$VolumeGroupUUID = $VolumeGroupList.entities | where { $_.name -eq "@@{Volume_GroupName}@@"} | select uuid
$VolumeGroupUUID = $VolumeGroupUUID.uuid

$VolumeGroupAttachURL = $VolumeGroupURL+"/$VolumeGroupUUID"+"/open"

$Body = @"
{
  "iscsi_client": {
    "client_address": "$MsSQL1_iSCSI_IQN"
  },
  "operation": "ATTACH"
}
"@

Invoke-RestMethod -Uri $VolumeGroupAttachURL -Method Post -TimeoutSec 100 -Headers $Header -ContentType $Type -Body $Body

sleep 5

