$VolumeGroupUUID = $VolumeGroupUUID.uuid

$VolumeGroupAttachURL = $VolumeGroupURL+"/$VolumeGroupUUID"+"/open"

$Body = @"
{
  "iscsi_client": {
    "client_address": "$MsSQL1_iSCSI_IQN"
  },
  "operation": "ATTACH"
}
"@

Invoke-RestMethod -Uri $VolumeGroupAttachURL -Method Post -TimeoutSec 100 -Headers $Header -ContentType $Type -Body $Body

sleep 5
