attrs.script:
#script

prism_ip = '@@{NTX_STORAGE_CLUSTER_URL}@@'
username = '@@{NTX_PRISM_USER_FOR_HYCU.username}@@'
password = '@@{NTX_PRISM_USER_FOR_HYCU.secret}@@'
vgname = '@@{NTX_VG_NAME}@@'
vg_uuid = ''
IQN='@@{INITIATOR_IQN}@@'

api_url = prism_ip + "/PrismGateway/services/rest/v2.0/"

print "INFO: Attaching VG {} to node.".format(vgname)

# -*- Check volume group existance

try:
  check_vg_url = api_url + "volume_groups"
  response = urlreq(check_vg_url, verb='GET', auth="BASIC", user=username, passwd=password, verify=False)
  vgs = json.loads(response.content)
  for vg in vgs['entities']:
    if vg['name'] == vgname:
      vg_uuid = vg['uuid']
except Exception as e:
	print "ERROR: Failed to get volume group, check volume group or check username/password"
	print e.message
	exit(1)

if vg_uuid == '':
  print "ERROR: Volume group with name {} doesn't exists".format(vgname)
  exit(1)

# -*- Attach Volume Group

attach_vg_url = api_url + "volume_groups/" + vg_uuid + "/open"
payload = {
  "iscsi_client": {
    "client_address": IQN
  },
  "operation": "ATTACH"
}

response = urlreq(attach_vg_url, verb='POST', auth="BASIC", user=username, passwd=password, params=json.dumps(payload), verify=False)
if response.status_code != 201:
	print "ERROR: Attaching vg '{}' failed.".format(vgname)

attach_status = json.loads(response.content)
if attach_status['value']:
  print "INFO: Attaching vg '{}' Successful.".format(vgname)
else:
  print "ERROR: Attaching vg '{}' failed.".format(vgname)

