#script

BASE_URL = "https://@@{address}@@" 
USER = "@@{USERNAME}@@"
PASSWD = "@@{PASSWORD}@@"
XPLORER_LOGLEVEL = @@{XPLORER_LOGLEVEL}@@
XFORMER_LOGLEVEL = @@{XFORMER_LOGLEVEL}@@
XPORTER_LOGLEVEL = @@{XPORTER_LOGLEVEL}@@
LOGIN = {"username":"@@{USERNAME}@@","password":"@@{PASSWORD}@@"}

headers = {'Content-Type': 'application/json', 'Accept': 'text/html'}

try:

    resp = urlreq(BASE_URL + '/sqlmover/v1/login', verb='POST',auth="BASIC", user=USER, passwd=PASSWD, params=json.dumps(LOGIN), headers=headers, verify=False)
    if resp.status_code == 200 :
        cookies = resp.cookies
    else:
        print "Login is unsuccessfull. Please check Xtract is up and reachable from Calm "


    if XPLORER_LOGLEVEL != "":
        url = BASE_URL + '/xplorer/v1/logging/level/'
        resp = urlreq(url, verb='POST',auth="BASIC", user=USER, passwd=PASSWD, params=json.dumps(XPLORER_LOGLEVEL), cookies=cookies, headers=headers, verify=False)
        resp_json = resp.json()
        if resp.status_code == 200:
            print resp_json
        else:
            print resp_json['message']

    if XFORMER_LOGLEVEL != "":
        url = BASE_URL + '/xformer/v1/logging/level/'
        resp = urlreq(url, verb='POST',auth="BASIC", user=USER, passwd=PASSWD, params=json.dumps(XFORMER_LOGLEVEL), cookies=cookies, headers=headers, verify=False)
        resp_json = resp.json()
        if resp.status_code == 200:
            print resp_json
        else:
            print resp_json['message']

    if XPORTER_LOGLEVEL != "":
        url = BASE_URL + '/xporter/v1/logging/level/'
        resp = urlreq(url, verb='POST',auth="BASIC", user=USER, passwd=PASSWD, params=json.dumps(XPORTER_LOGLEVEL), cookies=cookies, headers=headers, verify=False)
        resp_json = resp.json()
        if resp.status_code == 200:
            print resp_json
        else:
            print resp_json['message']            

except Exception as e:
    print "Error for Xtract  '%s'. %s" % (BASE_URL, e.message)
    exit(1)

