#!/bin/bash -xe
ORACLE_DB_URL="@@{ORACLE_DB_URL}@@"
ORACLE_DB_ARCHIVE=`echo "${ORACLE_DB_URL}" | awk 'BEGIN { FS = "[\\\\/]" }{print $NF}'`


sudo wget -P /sw "${ORACLE_DB_URL}"

sudo unzip -d /sw /sw/${ORACLE_DB_ARCHIVE}
sudo chown -R oracle:oinstall /sw/database

