attrs.script:
prism_ip = "@@{PC_IP}@@"
cluster_name = "@@{CLUSTER_NAME}@@"
nf_chain_name = "@@{NF_CHAIN_NAME}@@"


def crudops(api_url, payload):
  jwt = '@@{calm_jwt}@@'
  headers = {'Content-Type': 'application/json',  'Accept':'application/json', 'Authorization': 'Bearer {}'.format(jwt)}
  r = urlreq(api_url, verb='POST', params=json.dumps(payload), headers=headers, verify=False)
  if r.ok:
    resp = json.loads(r.content)
    return resp
  else:
    print "Post request failed", r.content
    exit(1)

def setconfig(api_url, payload):
  jwt = '@@{calm_jwt}@@'
  headers = {'Content-Type': 'application/json',  'Accept':'application/json', 'Authorization': 'Bearer {}'.format(jwt)}
  r = urlreq(api_url, verb='PUT', params=json.dumps(payload), headers=headers, verify=False)
  if r.ok:
    resp = json.loads(r.content)
    return resp
  else:
    print "PUT request failed", r.content
    exit(1)

print "Creating network function provider category"

category_api = 'https://{0}:9440/api/nutanix/v3/categories/network_function_provider'.format(prism_ip)
payload = {
"name": "network_function_provider"
}
setconfig(api_url=category_api, payload=payload)

print "Assigning value to network function provider category"
pan_api = 'https://{0}:9440/api/nutanix/v3/categories/network_function_provider/FortiGate'.format(prism_ip)
payload = {
"value": "FortiGate"
}
setconfig(api_url=pan_api, payload=payload)

print "Category list"
cat_list_api = 'https://{0}:9440/api/nutanix/v3/categories/network_function_provider/list'.format(prism_ip)
payload = {"kind": "category"}
print crudops(api_url=cat_list_api, payload=payload)


print "Get cluster list"
cluster_api = 'https://{0}:9440/api/nutanix/v3/clusters/list'.format(prism_ip)
payload = {"kind": "cluster"}
cluster_list = crudops(api_url=cluster_api, payload=payload)
for i in cluster_list['entities']:
  if i['spec']['name'] == cluster_name:
    print i
    cluster_uuid = i['metadata']['uuid']


print "Get NF list"
nf_list_api = 'https://{0}:9440/api/nutanix/v3/network_function_chains/list'.format(prism_ip)
payload = {}
nf_list = crudops(api_url=nf_list_api, payload=payload)
nf_exists = False
for i in nf_list['entities']:
  if i['status']['name'] == nf_chain_name:
    print i
    nf_exists = True
    nf_uuid = i['metadata']['uuid']

print "Creating/Updating service chain"
service_chain_api = 'https://{0}:9440/api/nutanix/v3/network_function_chains'.format(prism_ip)
payload = {
"spec": {
"name": nf_chain_name,
"resources": {
"network_function_list": [
{
"network_function_type": "INLINE",
"category_filter": {
"type": "CATEGORIES_MATCH_ANY",
"params": {"network_function_provider": ["FortiGate"]}
}
}
]
},
"cluster_reference": {
"kind": "cluster",
"name": cluster_name,
"uuid": cluster_uuid
}
},
"api_version": "3.1.0",
"metadata": {
"kind": "network_function_chain"
}
}

if nf_exists:
  print "Not creating chain as chain exists"
  #service_chain_api = service_chain_api + '/' + nf_uuid
  #setconfig(api_url=service_chain_api, payload=payload)
else:
  crudops(api_url=service_chain_api, payload=payload)
  print "Created service chain"

