attrs.script:
#script

prism_ip = '@@{NTX_STORAGE_CLUSTER_URL}@@'
username = '@@{NTX_CREDENTIALS.username}@@'
password = '@@{NTX_CREDENTIALS.secret}@@'
storage_container_uuid = '@@{CONTAINER_UUID}@@'
vgname = 'HYCU-Volume-Group'
target_vg_size = (@@{STORAGE_SIZE_TB}@@*1024*1024*1024*1024)*1.7
iqn = '@@{INITIATOR_IQN}@@'
count = 1
finish = 13

api_url = prism_ip + "/PrismGateway/services/rest/v2.0/"

check_vg_url = api_url + "volume_groups"
response = urlreq(check_vg_url, verb='GET', auth="BASIC", user=username, passwd=password, verify=False)
vgs = json.loads(response.content)

for vg in vgs['entities']:
  if vg['name'] == vgname:
    vgname = vgname + '-' + iqn

print "INFO: Creating volume group '{}'.".format(vgname)

create_vg_url = api_url + "volume_groups"
payload = {
  "disk_list": [{
    "create_config": {
      "size": target_vg_size,
      "storage_container_uuid": storage_container_uuid
      },
    "index": 0
    }],
    "is_shared": True,
    "name": vgname
}

while(True):
  count += 1
  response = urlreq(create_vg_url, verb='POST', auth="BASIC", user=username, passwd=password, params=json.dumps(payload), verify=False)
  if response.status_code == 201:
    print "INFO: Volume group '{}' successfully created".format(vgname)
    break
  else:
    print "ERROR: creation of volume group '{}' failed. Retrying in 5 seconds.".format(vgname)
    sleep(5)
  if count == finish:
    print "ERROR: creation of volume group '{}' failed. Terminating.".format(vgname)
    exit(1)

vg_create_status = json.loads(response.content)
task_uuid = vg_create_status['task_uuid']

sleep(5)

check_vg_creation_url = api_url + "tasks/" + task_uuid
response = urlreq(check_vg_creation_url, verb='GET', auth="BASIC", user=username, passwd=password, verify=False)
vg_create_status = json.loads(response.content)

if vg_create_status['progress_status'] != "Succeeded":
  print "ERROR: creation of volume group '{}' failed.".format(vgname)
  exit(1)
else:
  print "INFO: creation of volume group '{}' Successful.".format(vgname)

check_vg_url = api_url + "volume_groups"
response = urlreq(check_vg_url, verb='GET', auth="BASIC", user=username, passwd=password, verify=False)
vgs = json.loads(response.content)

for vg in vgs['entities']:
  if vg['name'] == vgname:
    iscsi_target=vg["iscsi_target"]
    vgUUID=vg['uuid']

print "INFO: Attaching VG {} to node.".format(vgname)

attach_url = check_vg_url + "/" + vgUUID + "/open"

payload = {
  "iscsi_client": {
    "client_address": iqn
  },
  "operation": "ATTACH"
}

response = urlreq(attach_url, verb='POST', auth="BASIC", user=username, passwd=password, params=json.dumps(payload), verify=False)
if response.status_code != 201:
  print "ERROR: Attaching vg '{}' failed.".format(vgname)
  print response.status_code
else:
  attach_status = json.loads(response.content)
  status = str(attach_status['value'])
  if status == 'True':
    print "INFO: Attaching vg '{}' Successful.".format(vgname)
    print "TARGET_IQN=iqn.2010-06.com.nutanix:" + iscsi_target
  else:
    print "ERROR: Attaching vg '{}' failed.".format(vgname)

